myApp.service("loginService",["$http","$location",function($http,$location){const vm=this;vm.user={username:"",email:"",password:""},vm.login=function(){""===vm.user.username||""===vm.user.password?swal("Please enter your username and password or register to continue!"):$http.post("/",vm.user).then(function(response){response.data.username?$location.path("/profile"):swal("Oops! You are not registered!")})},vm.registerUser=function(){""===vm.user.username||""===vm.user.password?swal("Choose a username and password!"):$http.post("/register",vm.user).then(function(response){$location.path("/home")},function(response){swal("That username or password is already taken! Please try again.")})}}]);